INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2018-10-10 10:57:00', '2018-10-10 02:57:00', '<h3>欢迎使用WordPress。</h3>\r\n\r\n<p>这是您的第一篇文章。<strong>编辑或删除它</strong>，然后开始写作吧！<img alt=\"\" src=\"/media/uploads/2018/10/10/jellyfish.jpg\" style=\"height:768px; width:1024px\" /></p>', '世界，您好！', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2018-10-10 15:08:00', '2018-10-10 07:08:00', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2018-10-10 10:57:00', '2018-10-10 02:57:00', '这是示范页面。页面和博客文章不同，它的位置是固定的，通常会在站点导航栏显示。很多用户都创建一个“关于”页面，向访客介绍自己。例如：\n\n<blockquote>欢迎！我白天是个邮递员，晚上就是个有抱负的演员。这是我的博客。我住在天朝的帝都，有条叫做Jack的狗。</blockquote>\n\n……或这个：\n\n<blockquote>XYZ Doohickey公司成立于1971年，自从建立以来，我们一直向社会贡献着优秀doohickies。我们的公司总部位于天朝魔都，有着超过两千名员工，对魔都政府税收有着巨大贡献。</blockquote>\n\n而您，作为一个WordPress用户，我们建议您访问<a href=\"http://localhost/wordpress/wp-admin/\">控制板</a>，删除本页面，然后添加您自己的页面。祝您使用愉快！', '示例页面', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2018-10-10 10:57:00', '2018-10-10 02:57:00', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(3, 1, '2018-10-10 10:57:15', '0000-00-00 00:00:00', '', '自动草稿', '', 'auto-draft', 'open', 'open', '', '', '', '', '2018-10-10 10:57:15', '0000-00-00 00:00:00', '', 0, 'http://localhost/wordpress/?p=3', 0, 'post', '', 0),
(4, 1, '2018-10-10 11:01:26', '2018-10-10 03:01:26', '1111', '111', '', 'publish', 'open', 'open', '', '111', '', '', '2018-10-10 11:01:26', '2018-10-10 03:01:26', '', 0, 'http://localhost/wordpress/?p=4', 0, 'post', '', 0),
(5, 1, '2018-10-10 11:01:26', '2018-10-10 03:01:26', '1111', '111', '', 'inherit', 'closed', 'closed', '', '4-revision-v1', '', '', '2018-10-10 11:01:26', '2018-10-10 03:01:26', '', 4, 'http://localhost/wordpress/2018/10/10/4-revision-v1/', 0, 'revision', '', 0),
(6, 1, '2018-10-10 15:08:33', '2018-10-10 07:08:33', '<h3>欢迎使用WordPress。</h3>\r\n这是您的第一篇文章。<strong>编辑或删除它</strong>，然后开始写作吧！', '世界，您好！', '', 'inherit', 'closed', 'closed', '', '1-revision-v1', '', '', '2018-10-10 15:08:33', '2018-10-10 07:08:33', '', 1, 'http://localhost/wordpress/2018/10/10/1-revision-v1/', 0, 'revision', '', 0);

